import {makeStyles} from '@material-ui/core/styles'
import {COLORS} from "../../constants";

const styles = makeStyles((theme) =>({
    navBar:{
        backgroundColor:COLORS.EBONY_CLAY,
        position: 'relative',
    },
    navBarBtn:{
        fontSize:17,
        color: '#fe4661',
        '&:hover':{
            opacity: 0.7,
        }
    },
    toolBer:{
        justifyContent:'space-between',
    },
    homeLink:{
        '&:hover': {
            color: '#fe4661',
            cursor:'pointer',
        }
    },
}))

export default styles;
