import {makeStyles} from '@material-ui/core/styles'
import {COLORS} from "../../constants";

const styles = makeStyles((theme) =>({
    title:{
        color:COLORS.EAST_BAY,
    },
    name:{
        color:COLORS.EAST_BAY,
        '&:hover':{
            color:COLORS.REDICAL_RED,
            cursor:'pointer',
        }
    },
    time:{
        color:COLORS.EAST_BAY,
    }

}));

export default styles;
